/*Table structure for table `visitor` */

DROP TABLE IF EXISTS `visitor`;

CREATE TABLE `visitor` (
  `id` bigint(20) NOT NULL AUTO_INCREMENT,
  `uuid` varchar(36) NOT NULL COMMENT '访客标识码',
  `ip` varchar(255) DEFAULT NULL COMMENT 'ip',
